const state = { 
    events: [{
        "kind": "calendar#event",
        "etag": "\"3482412962369534\"",
        "id": "1t75pke2vd6vr9fd6qcpie2lh4",
        "status": "confirmed",
        "htmlLink": "https://www.google.com/calendar/event?eid=MXQ3NXBrZTJ2ZDZ2cjlmZDZxY3BpZTJsaDQgemVyb3hoYW5rQG0",
        "created": "2025-03-05T20:28:00.000Z",
        "updated": "2025-03-05T20:28:01.184Z",
        "summary": "Dinner!",
        "start": { "dateTime": "2025-03-05T19:00:00-05:00", "timeZone": "America/New_York" },
        "end": { "dateTime": "2025-03-05T20:00:00-05:00", "timeZone": "America/New_York" }
    }],
    currentTime: new Date().toISOString(),
    timezone: 'America/New_York'
};

const eventsList = document.getElementById('events-list');
const currentDate = new Date();
const midnight = new Date(currentDate);
midnight.setHours(24, 0, 0, 0);

state.events.forEach(event => {
    const eventStart = new Date(event.start.dateTime);
    if (eventStart >= currentDate && eventStart <= midnight) {
        const div = document.createElement('div');
        div.className = 'p-2 border rounded';
        div.innerHTML = `<a href='${event.htmlLink}' class='text-blue-600'>${event.summary} - ${eventStart.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</a>`;
        eventsList.appendChild(div);
    }
});
